import React from "react";
// @flow
const { Component, Fragment } = React;

const { css, cx, injectGlobal } = emotion;

const KEYCODES = {
  tab: 9,
  esc: 27,
  arrowUp: 38,
  arrowRight: 39,
  arrowDown: 40,
  arrowLeft: 37,
};

class Header extends Component {
  render() {
    const {
      withDrawer,
      drawerAction,
      drawerActive,
      a11yDrawerTitle,
      children,
      drawerTriggerRef,
    } = this.props;

    const headerContainer = css`
      position: fixed;
      width: 100%;
      top: 0;
    `;

    const header = css`
      background: #d0d0d0;
      background: rgba(35, 55, 70, 1);
      padding: 15px;
      height: 22px;
      color: #fff;
    `;

    const DrawerTrigger = React.forwardRef((props, ref) => (
      <button
        disabled={drawerActive}
        ref={ref}
        onClick={props.onClick}
        aria-label="Open, Navigation Menu"
      >
        ☰
      </button>
    ));

    return (
      <header className={headerContainer}>
        <div className={header}>
          {drawerAction && (
            <DrawerTrigger
              disabled={drawerActive}
              ref={drawerTriggerRef}
              onClick={() => drawerAction(this.triggerElement)}
              aria-label="Open, Navigation Menu"
            />
          )}
        </div>
        {children}
      </header>
    );
  }
}

/*
 * # MenuDrawer
 * A header related component which animates in and out when a user requests it. It is used with a hamburger menu button.
 */
class MenuDrawer extends Component {
  handleKeydown = (event) => {
    const { esc } = KEYCODES;
    const pressedEsc = event.key === "Escape" || event.keyCode === esc;

    if (pressedEsc) {
      this.props.drawerAction();
    }
  };

  render() {
    const { a11yTitle, drawerAction, children, drawerActive } = this.props;

    const style = {
      drawer: css`
        background: #fff;
        box-shadow: 0 0 3px rgba(0, 0, 0, 0.15);
        padding: 10px;
        width: 100%;
        max-width: 375px;
        height: 100vh;
        position: fixed;
        top: 0;
        transition: 0.33s visibility step-end, 0.33s transform ease;
      `,
      drawerInactive: css`
        visibility: hidden;
        transform: translateX(-100%);
      `,
      drawerActive: css`
        visibility: visible;
        transform: translateX(0%);
        transition: 0.33s visibility step-start, 0.33s transform ease;
      `,
      btnClose: css`
        position: absolute;
        top: 1rem;
        right: 1rem;
      `,
    };

    const drawerClass = cx({
      [style.drawer]: true,
      [style.drawerActive]: drawerActive,
      [style.drawerInactive]: !drawerActive,
    });

    return (
      <FocusTrap
        id="menu"
        className={drawerClass}
        isTrapped={drawerActive}
        handleKeydown={this.handleKeydown}
      >
        <nav aria-labelledby="drawer-menu-label">
          <h3 id="drawer-menu-label" className="visually-hidden">
            {a11yTitle}
          </h3>
          <button
            className={style.btnClose}
            aria-label="Close"
            onClick={() => drawerAction()}
          >
            ×
          </button>
          {children}
        </nav>
      </FocusTrap>
    );
  }
}

/*
 * # MenuWaypoint
 * A second level header component which provides information on which step of the wizard rendered in the MainContainer space
 */

/*
 * # Main
 * A container component is the main content area for our applications.
 */
export class Main extends Component {
  render() {
    const { children, ariaHidden } = this.props;

    const mainStyle = css`
      max-width: 486px;
      padding: 100px 15px 0;
      padding-top: 100px;
      margin: 0 auto;
      border-top: 1px solid red;
    `;

    return (
      <div className={mainStyle} aria-hidden={ariaHidden} role="main">
        {children}
      </div>
    );
  }
}

/*
 * # Focus Trap
 * A container component that traps tabfocus within its bounds when the `isTrapped` prop is set to `true`
 */
class FocusTrap extends Component {
  constructor(props) {
    super(props);
    this.elements = [];
  }

  componentDidMount() {
    const node = ReactDOM.findDOMNode(this);

    if (node instanceof HTMLElement) {
      this.elements = node.querySelectorAll(
        'a[href]:not([disabled]), button:not([disabled]), textarea:not([disabled]), input[type="text"]:not([disabled]), input[type="radio"]:not([disabled]), input[type="checkbox"]:not([disabled]), select:not([disabled])'
      );
      node.addEventListener("keydown", this.handleKeydown);
    }
  }

  componentDidUpdate(prevProps) {
    const { isTrapped } = this.props;
    if (isTrapped) {
      this.elements[0].focus();
    }
  }

  handleKeydown = (event) => {
    const { tab } = KEYCODES;
    const { handleKeydown } = this.props;
    const elements = this.elements;
    const activeElement = document.activeElement;
    const firstFocusable = elements[0];
    const lastFocusable = elements[elements.length - 1];
    const isTabPressed = event.key === "Tab" || event.keyCode === tab;

    if (typeof handleKeydown === "function") {
      handleKeydown(event);
    }

    if (!isTabPressed) {
      return;
    }

    if (event.shiftKey) {
      /* shift + tab */ if (activeElement === firstFocusable) {
        lastFocusable.focus();
        event.preventDefault();
      }
    } /* tab */ else {
      if (activeElement === lastFocusable) {
        firstFocusable.focus();
        event.preventDefault();
      }
    }
  };

  render() {
    const { children, isTrapped, ...rest } = this.props;

    return <div {...rest}>{children}</div>;
  }
}

/*
 * # App
 * _What follows is for demo purposes only_
 */

export class Ham extends Component {
  constructor(props) {
    super(props);

    this.drawerTriggerRef = React.createRef();

    this.state = {
      drawerActive: false,
    };
  }

  handleDrawer = (triggerElement) => {
    this.setState(
      (prevState) => ({
        drawerActive: !prevState.drawerActive,
      }),
      () => {
        const { drawerActive } = this.state;
        if (!drawerActive) {
          this.drawerTriggerRef.current.focus();
        }
      }
    );
  };

  render() {
    const { drawerActive } = this.state;

    return (
      <Fragment>
        <Header
          drawerAction={this.handleDrawer}
          drawerActive={drawerActive}
          a11yDrawerTitle="Navigation Menu"
          drawerTriggerRef={this.drawerTriggerRef}
        >
          {false && (
            <MenuWaypoint
              a11yDrawerTitle="Navigation Menu"
              drawerAction={this.handleDrawer}
              drawerActive={drawerActive}
              id="menu"
            />
          )}
        </Header>
        <MenuDrawer
          a11yTitle="Navigation Menu"
          drawerAction={this.handleDrawer}
          drawerActive={drawerActive}
          id="menu"
        >
          <ul>
            <li>
              <a href="#Home">Home</a>
            </li>
            <li>
              <a href="#About">About</a>
            </li>
            <li>
              <a href="#FAQ">FAQ</a>
            </li>
            <li>
              <a href="#Contact">Contact</a>
            </li>
            <li>
              <a href="#Login">Login</a>
            </li>
          </ul>
        </MenuDrawer>
      </Fragment>
    );
  }
}
